package String;

public class ToUpperCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1= " testyantra ";
		String s2="TestYANTRA";
		System.out.println(s1.toUpperCase());// convert the lower case value of string to upper case
		System.out.println(s2.toLowerCase());// convert the upper case value of string to lower case

	}

}
